import React, { useState,useEffect } from "react";
import './App.css';
// Components
import Form from "./components/Form";
import TodoList from "./components/TodoList";
import Todo from "./components/Todo";

function App() {
  // use effect
  useEffect(() => {
    filterHandler();
  }, [todos, status]);
  // state
  const [inputText, setInputText] = useState("");
  const [todos, setTodos] = useState([]);
  const [status, setStatus] = useState('all');
  const [filteredTodos, setFilteredTodos] = useState([]);
  // functions
  const filterHandler = () => {
    switch(status) {
      case 'completed':
        setFilteredTodos(todos.filer(todo => todo.completed === true));
        break;
      case 'uncompleted':
        setFilteredTodos(todos.filer(todo => todo.completed === false));
        break;
      default:
        setFilteredTodos(todos);
        break;
    }
  };

  return (
    <div className="App">
      <header>
        <h1>Vishnu's Todo List</h1>
      </header>
      <Form
        inputText={inputText}
        todos={todos}
        setTodos={setTodos}
        setInputText={setInputText}
        setStatus={setStatus}
      />
      <TodoList
        filteredTodos={filteredTodos}
        setTodos={setTodos}
        todos={todos}
      />
    </div>
  );
};

export default App;
